using System; using System.Collections.Generic; using City_Center.Clases; using Xamarin.Forms; using City_Center.PopUp; using Rg.Plugins.Popup.Extensions;  namespace City_Center.Page {     public partial class DetalleHotel : ContentPage     {         string[] ListaOpciones;          public WebViewHotel _webHotel;                  public DetalleHotel()         {             InitializeComponent();             _webHotel = new WebViewHotel();              NavigationPage.SetTitleIcon(this, "logo.png");                        }          protected override void OnAppearing()         {             base.OnAppearing();              ListaOpciones = new string[] { VariablesGlobales.Img1, VariablesGlobales.Img2, VariablesGlobales.Img3 };              listaDetalleHabitacion.ItemsSource = ListaOpciones;          }          void Handle_ItemSelected(object sender, Xamarin.Forms.SelectedItemChangedEventArgs e)         {             try             {                 var Seleccion = e.SelectedItem;                   if (Seleccion != null)                 {                     var selection = e.SelectedItem;                      Img1provisional.Source = selection.ToString();                 }              }             catch (Exception ex)             {              }           }

        async void BtnReservaOnline(object sender, System.EventArgs e)         {             try             {                 VariablesGlobales.FechaInicio = DateTime.Today;                 VariablesGlobales.FechaFin = DateTime.Today.AddDays(2);                 VariablesGlobales.NumeroHuespedes = Convert.ToInt32(2);                 await Navigation.PushPopupAsync(_webHotel);             }             catch (Exception ex)             {                 await DisplayAlert("Error", "E: " + ex.ToString(), "ok");             }          }     } } 